mapper.select:
select ps.Id, ps.MemberId, ps.ProjectId, ps.CreateTime, ps.IsDel, p.ProjectName, p.projectStatus,p.PreheatTime as projectBeginDate,p.ProjectImage from projectfavorite ps left join project p on ps.ProjectId=p.Id where ps.memberid = #{memberid,jdbcType=BIGINT}
and p.projectStatus=#{projectStatus,jdbcType=SMALLINT}
order by ps.CreateTime desc

mapper.update:
update projectfavorite
set MemberId = #{memberid,jdbcType=BIGINT},
  ProjectId = #{projectid,jdbcType=BIGINT},
  CreateTime = #{createtime,jdbcType=TIMESTAMP},
  IsDel = #{isdel,jdbcType=INTEGER}
where Id = #{id,jdbcType=BIGINT}

mapper.sql:
a.IsDel = 0

	and a.ProjectId=${projectid}


