mapper.select:
select

from project
where memberid = #{memberid,jdbcType=BIGINT}

	and projectstatus=#{projectStatus,jdbcType=SMALLINT}

mapper.sql:
1=1

	and id=${id}


	and typeId=${typeId}


	and auditStatus=${auditStatus}


	and projectStatus=${projectStatus}


	and projectName like ''%${projectName}%''


	and endTime>=''${startTime}''


	and date_add(''${endTime}'', INTERVAL 1 day)>startTime


	and createTime>=''${startCreateTime}''


	and date_add(''${endCreateTime}'', INTERVAL 1
	day)>createTime

1=1
and projectStatus=${projectStatus}
AND p.IsShelve = 1 AND p.ProjectStatus != 1 AND p.ProjectStatus != 5 AND p.ProjectStatus != 4 AND p.IsNovice!=1 AND p.AuditStatus = 2 AND NOW() >= p.PreheatTime AND p.EndTime >= NOW()

mapper.update:
update project set AuditStatus = #{auditStatus,jdbcType=SMALLINT},
PreheatTime = #{onlinePreheatDate,jdbcType=TIMESTAMP}, StartTime = #{openFinancingDate,jdbcType=TIMESTAMP}, EndTime = #{endFinancingDate,jdbcType=TIMESTAMP},
AuditName = #{auditName,jdbcType=VARCHAR}, AuditTime = now() where Id = #{projectId,jdbcType=BIGINT}
